package com.example.sportfinder.view;

import android.content.Intent;
import android.os.Bundle;
import android.view.View;

import androidx.appcompat.app.AppCompatActivity;
import androidx.recyclerview.widget.LinearLayoutManager;
import androidx.recyclerview.widget.RecyclerView;

import com.example.sportfinder.R;
import com.example.sportfinder.data.localdatabase.Localizacao;

import java.util.ArrayList;
import java.util.List;

public class Fit4You extends AppCompatActivity {

    @Override
    protected void onCreate(Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);
        setContentView(R.layout.fit4you);


        List<Localizacao> localizacaoList = new ArrayList<>();localizacaoList.add(new Localizacao("Fit4You",
                "data:image/png;base64,"
                ," R. do Fomento n 10, Beja"," https://fit4you.pt"," +351 965 839 408","Ginásio"));

        LocalizacaoAdapter localizacaoAdapter = new LocalizacaoAdapter(localizacaoList);

        RecyclerView recyclerViewLocalizacao = findViewById(R.id.recyclerview_localização1);
        recyclerViewLocalizacao.setLayoutManager(new LinearLayoutManager(this, LinearLayoutManager.HORIZONTAL, false));
        recyclerViewLocalizacao.setAdapter(localizacaoAdapter);
    }

    public void Spots(View view) {
        Intent intent = (new Intent(Fit4You.this, PesquisaActivity.class));
        startActivity(intent);
    }

    public void Home(View view) {
        Intent intent = (new Intent(Fit4You.this, MainActivity.class));
        startActivity(intent);
    }

    public void Tutorial(View view) {
        Intent intent = (new Intent(Fit4You.this, tutorialActivity.class));
        startActivity(intent);
    }

}
